import{H as n,a as l,o as a,c as e,b as s,I as o,e as _,J as i}from"./index-f355f8eb.js";const c="/Dr_Sevar_Nigmatovna/assets/blefoplast1-eaf8732c.jpg",d="/Dr_Sevar_Nigmatovna/assets/blefoplast2-fb70bf95.jpg",r="/Dr_Sevar_Nigmatovna/assets/blefoplast3-258240cb.jpg",h="/Dr_Sevar_Nigmatovna/assets/blefoplast4-cc715809.jpg",f="/Dr_Sevar_Nigmatovna/assets/blefoplast5-d7f9b1a1.jpg",m={},p={class:"container"},b={class:"info"},u=s("img",{src:c,alt:"",class:"info__images"},null,-1),g=s("h1",{class:"info__title"},"Косметологическая Блефаропластика",-1),v=s("h3",{class:"info__headline"},"Виды безоперационной блефаропластики",-1),x={class:"info__text"},N=s("span",{class:"bold"},"Инъекционные методики",-1),S=s("br",null,null,-1),j=s("br",null,null,-1),D=s("span",{class:"bold"}," мезотерапия, биоревитализация, контурная пластика, ботулинотерапия.",-1),B=s("br",null,null,-1),V=s("br",null,null,-1),k=s("span",{class:"bold"}," Мезотерапия ",-1),A=s("br",null,null,-1),C=s("br",null,null,-1),E=s("br",null,null,-1),F=s("span",{class:"bold"}," Биоревитализация ",-1),H=s("br",null,null,-1),I=s("div",{class:"info__text-img"},[s("img",{src:d,alt:"",class:"info__text-img"})],-1),J={class:"info__text"},M=s("span",{class:"bold"}," Контурная пластика ",-1),R=s("br",null,null,-1),T=s("br",null,null,-1),q=s("br",null,null,-1),w=s("span",{class:"bold"}," Ботулинотерапия ",-1),y=s("br",null,null,-1),z=s("br",null,null,-1),G=s("br",null,null,-1),K=i('<h3 class="info__headline">Ультразвуковое воздействие</h3><p class="info__text"> Безоперационный SMAS-лифтинг верхних и нижних век выполняется высокофокусированными ультразвуковыми волнами. Эффект подтяжки кожи вокруг глаз таким способом схож с эффектом пластической операции, но процедура менее травматична, не требует долгого восстановления. УЗ-волны воздействуют не только на кожу, но и на ее каркас – мышечно-апоневротический слой. На нем образуются микроожоги – точки термической коагуляции, которые стимулируют образование коллагеновых и эластиновых волокон, регенерацию кожного покрова. </p><h3 class="info__headline">Термолифтинг (термаж)</h3><p class="info__text"> Термолифтинг (RF-лифтинг) основан на прогреве глубоких слоев дермы радиочастотными токами. Улучшение крово- и лимфообращения в зоне воздействия обеспечивает эффект подтяжки век без операции. Локальное прогревание также способствует усиленной выработке коллагена и эластина. Как следствие, кожа становится более подтянутой и упругой, уменьшается отечность, разглаживаются «гусиные лапки» в уголках глаз. <br> <br> Для достижения стойкого эффекта рекомендуется пройти курс из 5-8 процедур. Результат, в зависимости от исходного состояния кожи и других факторов, сохраняется до 1,5-3 лет. </p><h3 class="info__headline">Нитевая блефаропластика</h3><p class="info__text"> Безоперационная подтяжка век с помощью биосовместимых нитей, которые вводятся под кожу и поддерживают ее в нужном положении. Нити могут быть рассасывающимися, на месте которых со временем формируется каркас из собственных коллагеновых волокон, или нерассасывающимися. Нити или возникший на их месте соединительнотканный каркас препятствуют деформации и смещению тканей. <br> <br> Нитевая блефаропластика позволяет без операции слегка приподнять опущенные наружные уголки глаз, устранить нависание верхнего века, убрать мешки под глазами. Результат процедуры сохраняется от 2 до 5 лет. Для большего эффекта введение нитей рекомендуется сочетать с другими техниками омоложения – инъекционными, аппаратными. </p><h3 class="info__headline">Результат процетуры</h3><div class="info__text-img"><img src="'+r+'" alt="" class="info__text-img"><img src="'+h+'" alt="" class="info__text-img"><img src="'+f+'" alt="" class="info__text-img"></div>',8);function L(O,P){const t=l("fa");return a(),e("div",p,[s("div",b,[u,g,v,s("p",x,[N,o(),S,o(),j,o(" Общее название нескольких процедур, которые помогают устранить различные проблемы в периорбитальной зоне. К ним относятся:"),D,o(" В целом инъекционные методики направлены на безоперационный лифтинг век – легкую подтяжку кожи, улучшение ее качества. "),B,o(),V,_(t,{icon:"diamond",class:"dimond__icon"}),o(),k,o(),A,o(" Внутрикожное введение специальных коктейлей, содержащих гиалуроновую кислоту, витамины, пептиды, другие полезные вещества. Помогает увлажнить кожу в периорбитальной области, сделать ее более плотной, сглаживает мелкие морщинки. В результате питания и уплотнения кожного покрова мешки под глазами становятся менее заметными. "),C,o(),E,_(t,{icon:"diamond",class:"dimond__icon"}),o(),F,o(),H,o(" Имеет тот же принцип действия, что и мезотерапия, но для введения используются препараты, содержащие только низкомолекулярную гиалуроновую кислоту гелевой консистенции. В результате процедуры сглаживается сетка мелких морщин, уменьшаются носослезные борозды, отечность, темные круги под глазами. ")]),I,s("p",J,[_(t,{icon:"diamond",class:"dimond__icon"}),o(),M,o(),R,o(" Подкожное введение филлеров на основе гиалуроновой кислоты. От биоревитализации процедура отличается глубиной введения филлера, а также тем, что для нее используется гиалуроновая кислота более плотной консистенции. В периорбитальной области контурная пластика используется для коррекции объема в зоне нижнего века, выравнивания рельефа. Также она обеспечивает легкий лифтинг кожи вокруг глаз без операции, в результате чего сглаживаются носослезные борозды. "),T,o(),q,_(t,{icon:"diamond",class:"dimond__icon"}),o(),w,o(),y,o(" Подкожное или внутримышечное введение стабилизированного и очищенного от примесей ботулотоксина типа А. Под действием препаратов ботулотоксина расслабляются мимические мышцы. Процедура помогает скорректировать опущение наружного угла глаза, сгладить мимические морщины, исправить асимметричное опущение века. При инъекциях в область лба и межбровной складки, слегка поднимаются брови, взгляд становится более открытым. "),z,o(),G,o(" Эффект инъекционных методик сохраняется от 3 месяцев до 1-1,5 лет. Мезотерапию, биоревитализацию рекомендуется делать курсом. Длительность результата этих процедур зависит в том числе и от исходного состояния кожи. Эффект контурной пластики длится 1-1,5 года, дольше – после повторного введения филлеров. Ботулинотерапия обеспечивает результат на 3-6 месяцев, реже – до года. ")]),K])])}const U=n(m,[["render",L]]);export{U as default};
