import{H as t,a as c,o as a,c as l,b as s,e as _,I as o,J as i,_ as d}from"./index-f355f8eb.js";const e="/Dr_Sevar_Nigmatovna/assets/smas1-4560efa1.jpg",m="/Dr_Sevar_Nigmatovna/assets/smas2-edcc70a4.jpg",r="/Dr_Sevar_Nigmatovna/assets/smas3-3323f1dc.jpg",h="/Dr_Sevar_Nigmatovna/assets/smas4-21e7ad49.jpg",f={},u={class:"container"},p={class:"info"},S=i('<img src="'+d+'" alt="" class="info__images"><h1 class="info__title">Что такое SMAS-лифтинг (фейслифтинг)?</h1><p class="info__text"> SMAS-лифтинг – процедура по подтяжке лица без хирургического вмешательства с помощью ультразвукового аппарата. <br><br> В процессе используются ультразвуковые волны, ведь именно они могут проникнуть глубоко под кожу. Воздействие на глубинные слои кожи обеспечивает более качественный и стойкий эффект. <br><br> Разберемся по порядку, как именно проходит процедура. Ультразвук проникает в кожу примерно на глубину до 3,5 мм в лицевой зоне и до 13 мм на теле, доходя до SMAS-слоя. Он состоит из мышц, коллагена и эластиновых волокон. Ухудшение состояния этого слоя приводит к потере тонуса кожи, появлению морщин, изменениям в строении уголков рта и век, обвисанию кожи на бедрах.</p><div class="info__text-img"><img src="'+e+'" alt="" class="info__text-img"></div><p class="info__text">Ультразвук стимулирует синтез коллагена и эластиновых волокон, в результате кожа начинает регенерировать, то есть восстанавливается овал лица, исчезают морщины и обвисшие участки, возвращается общее здоровье кожи.</p><h3 class="info__headline">Показания к SMAS-лифтингу</h3><p class="info__text">Основными показаниями к проведению SMAS-лифтинга являются:</p><br>',8),x={class:"info__text"},g={class:"info__text"},b={class:"info__text"},v={class:"info__text"},A={class:"info__text"},M={class:"info__text"},N=s("div",{class:"info__text-img"},[s("img",{src:m,alt:"",class:"info__text-img"})],-1),j=s("p",{class:"info__text"},"Ультразвук помогает избавиться от отложений жира, который располагается между дермой и SMAS-слоем. На щеках это большая часть жира, на подбородке и вокруг глаз его доля меньше, но процедура все равно пройдет эффективно.",-1),D=s("h3",{class:"info__headline"},"Противопоказания к SMAS-лифтингу",-1),V={class:"info__text"},B=s("br",null,null,-1),k=s("br",null,null,-1),C=s("br",null,null,-1),E=s("br",null,null,-1),H=s("br",null,null,-1),I=s("br",null,null,-1),J=s("br",null,null,-1),L=s("br",null,null,-1),T=s("br",null,null,-1),q=s("br",null,null,-1),w=s("br",null,null,-1),y=s("br",null,null,-1),z=s("br",null,null,-1),F=s("br",null,null,-1),G=s("br",null,null,-1),K=i('<h3 class="info__headline">Процедура SMAS-лифтинга: плюсы и минусы</h3><p class="info__text">Любая косметологическая процедура, независимо от ее характера, имеет свои преимущества или недостатки. Минусы и плюсы SMAS-лифтинга определяются с точки зрения эффективности, противопоказаний, цены, результата, периода сохранения результата, периода реабилитации и других важных критериев. Очень важно, чтобы каждый пациент был полностью проинформирован до проведения процедуры, однако при появлении вопросов всегда можно обратиться к нашим врачам и уточнить интересующие подробности.</p><h3 class="info__headline">Результат процетуры</h3><div class="info__text-img"><img src="'+r+'" alt="" class="info__text-img"><img src="'+h+'" alt="" class="info__text-img"></div>',4);function O(P,Q){const n=c("fa");return a(),l("div",u,[s("div",p,[S,s("p",x,[_(n,{icon:"diamond",class:"dimond__icon"}),o(" отеки; ")]),s("p",g,[_(n,{icon:"diamond",class:"dimond__icon"}),o(" малярные мешки; ")]),s("p",b,[_(n,{icon:"diamond",class:"dimond__icon"}),o(" сниженный тонус кожи; ")]),s("p",v,[_(n,{icon:"diamond",class:"dimond__icon"}),o(" большая жировая прокладка в области щек; ")]),s("p",A,[_(n,{icon:"diamond",class:"dimond__icon"}),o(" жировые отложения в области подбородка и живота в сочетании со слабой кожей на этих участках; ")]),s("p",M,[_(n,{icon:"diamond",class:"dimond__icon"}),o(" нечеткий овал лица. ")]),N,j,D,s("p",V,[o("Несмотря на большую пользу smas лифтинга, противопоказания все же имеются. Фейслифтинг нельзя делать людям, не достигшим возраста 18 лет. "),B,o(),k,o(" Также процедуру запрещено проводить при: "),C,E,_(n,{icon:"diamond",class:"dimond__icon"}),o(" беременности; "),H,_(n,{icon:"diamond",class:"dimond__icon"}),o(" лактации;"),I,_(n,{icon:"diamond",class:"dimond__icon"}),o(" онкологических заболеваниях;"),J,_(n,{icon:"diamond",class:"dimond__icon"}),o(" инфекционных заболеваниях;"),L,_(n,{icon:"diamond",class:"dimond__icon"}),o(" заболеваниях сердечно-сосудистой и эндокринной систем;"),T,_(n,{icon:"diamond",class:"dimond__icon"}),o(" использовании кардиостимулятора; "),q,_(n,{icon:"diamond",class:"dimond__icon"}),o(" наличии металлических имплантов в зоне воздействия ультразвука, кроме зубных протезов;"),w,_(n,{icon:"diamond",class:"dimond__icon"}),o(" гнойных воспалениях; "),y,_(n,{icon:"diamond",class:"dimond__icon"}),o(" акне и угрях; "),z,_(n,{icon:"diamond",class:"dimond__icon"}),o("предрасположенности к появлению келоидных рубцов. "),F,o(),G,o(" При наличии хотя бы одного из пунктов следует сначала записаться на консультацию. Врач решит, можно ли проводить процедуру, при необходимости назначит дополнительный курс лечения. ")]),K])])}const U=t(f,[["render",O]]);export{U as default};
